// --- Password Validation ---
// 1. Check password length >= 8
if (!password || password.length < 8) {
    return {
        isError: true,
        errorMessage: "A senha deve ter no mínimo 8 caracteres."
    };
}

// 2. Check for at least 1 number
const hasNumber = /\d/.test(password);
if (!hasNumber) {
    return {
        isError: true,
        errorMessage: "A senha deve conter pelo menos 1 número."
    };
}

// 3. Check for at least 1 uppercase letter
const hasUpperCase = /[A-Z]/.test(password);
if (!hasUpperCase) {
    return {
        isError: true,
        errorMessage: "A senha deve conter pelo menos 1 letra maiúscula."
    };
}

// 4. Check for at least 1 lowercase letter
const hasLowerCase = /[a-z]/.test(password);
if (!hasLowerCase) {
    return {
        isError: true,
        errorMessage: "A senha deve conter pelo menos 1 letra minúscula."
    };
}

// If all validations pass, return success object
return {
    isError: false,
    errorMessage: ""
};

