const gtinNumber = context.local.data?.['form']?.['formData']?.['p-gtin'];

if (!gtinNumber) {
  return ''; // Retorna string vazia se o GTIN for nulo ou indefinido
}

// Converte o GTIN para string para garantir que o método padStart possa ser usado.
// Remove quaisquer caracteres não numéricos antes de preencher, para maior segurança.
const gtinString = String(gtinNumber).replace(/\D/g, '');

// Usa padStart para preencher com zeros à esquerda até atingir 14 caracteres.
// Se a string já tiver 14 ou mais caracteres, ela não será alterada.
return gtinString.padStart(14, '0');

