function toSlug(text) {
  if (!text) {
    return '';
  }

  // 1. Converte para string e minúsculas
  let slug = String(text).toLowerCase();

  // 2. Remove acentos e caracteres especiais (normalização Unicode)
  // NFD: Normaliza para forma de decomposição de compatibilidade canônica
  // /[\u0300-\u036f]/g: Remove caracteres diacríticos (acentos)
  slug = slug.normalize('NFD').replace(/[\u0300-\u036f]/g, '');

  // 3. Substitui caracteres não alfanuméricos (exceto hífens e números) por hífens
  // `[^a-z0-9 -]` - remove tudo que não for letra de a-z, número, espaço ou hífen
  slug = slug.replace(/[^a-z0-9 -]/g, '');

  // 4. Substitui espaços e múltiplos hífens por um único hífen
  slug = slug.replace(/\s+/g, '-').replace(/-+/g, '-');

  // 5. Remove hífens do início e do fim
  slug = slug.replace(/^-+|-+$/g, '');

  return slug;
}

