function formatDateTimeForTimeZone(dateString, userTimeZone) {
  if (!dateString) {
    return ''; // Retorna string vazia se a data for nula ou indefinida
  }

  // Fuso horário padrão para o Brasil
  const defaultTimeZone = "America/Sao_Paulo";
  // Usa o fuso horário do usuário se fornecido e válido, senão usa o padrão
  const targetTimeZone = (userTimeZone && userTimeZone.trim() !== '') ? userTimeZone : defaultTimeZone;

  try {
    const date = new Date(dateString);

    // Verifica se a data é válida
    if (isNaN(date.getTime())) {
      return "Data inválida"; // Retorna uma mensagem de erro se a data for inválida
    }

    // Opções de formatação para DD/MM/YYYY HH:MM (sem segundos)
    const options = {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit',
      hour12: false, // Usar formato 24 horas
      timeZone: targetTimeZone // Aplica o fuso horário desejado
    };

    // Formata a data e hora para o padrão brasileiro no fuso horário alvo
    const formattedDate = new Intl.DateTimeFormat('pt-BR', options).format(date);

    return formattedDate;

  } catch (error) {
    // Captura erros na criação ou formatação da data
    return "Erro ao formatar data";
  }
}

