function formatarTelefoneParaAPI(phoneNumber) {
  if (!phoneNumber) {
    return ''; // Retorna string vazia se o número for nulo ou indefinido
  }

  // Garante que é uma string e remove todos os caracteres não numéricos
  const cleanedPhone = String(phoneNumber).replace(/\D/g, '');

  // Adiciona '55' no início, se o número limpo não for vazio
  // E assume que o DDD já está incluso no cleanedPhone
  return cleanedPhone ? '55' + cleanedPhone : '';
}

