function isBrazilianMobile(phoneNumber) {
  if (!phoneNumber) {
    return false; // Retorna falso se o número for nulo ou vazio
  }

  // Remove todos os caracteres não numéricos (parênteses, espaços, traços, etc.)
  const cleanedPhone = String(phoneNumber).replace(/\D/g, '');

  // Valida o padrão de número de celular brasileiro:
  // ^       - Início da string
  // \d{2}   - Dois dígitos para o DDD
  // 9       - O dígito '9' obrigatório para celular
  // \d{8}   - Oito dígitos restantes do número
  // $       - Fim da string
  const regex = /^\d{2}9\d{8}$/;

  return regex.test(cleanedPhone);
}

const contactPhone = context.local.data?.['form']?.['formData']?.['contact_phone'];

return isBrazilianMobile(contactPhone);

