function encodeAddressForURLFromCEPVariable(cepData) {
  // Map the cepData properties to the desired address fields
  // Using the exact property names from your cepData example
  const addressFields = {
    zip_code: cepData.cep, // 'cep' from cepData maps to 'zip_code'
    street: cepData.logradouro, // 'logradouro' from cepData maps to 'street'
    complement: cepData.complemento, // 'complemento' from cepData maps to 'complement'
    neighborhood: cepData.bairro, // 'bairro' from cepData maps to 'neighborhood'
    city: cepData.localidade, // 'localidade' from cepData maps to 'city'
    state: cepData.uf // 'uf' from cepData maps to 'state'
    // You might also want to include 'number' here if it's available elsewhere or dynamically
    // For this example, 'number' is not in the provided cepData, so it's omitted
  };

  const parts = [];
  for (const key in addressFields) {
    if (addressFields.hasOwnProperty(key) && addressFields[key] !== null && addressFields[key] !== undefined) {
      parts.push(encodeURIComponent(key) + '=' + encodeURIComponent(String(addressFields[key]).trim())); // Ensure value is string before trim
    }
  }
  return parts.join('&');
}

