function isCEPValido(cep) {
  if (!cep) {
    return false; // Retorna falso se o CEP for nulo ou indefinido
  }

  // Remove caracteres não numéricos
  const cleanedCEP = cep.replace(/\D/g, '');

  // Verifica se o CEP tem o formato correto (5 dígitos, um hífen e 3 dígitos)
  return /^\d{5}-\d{3}$/.test(cep) || /^\d{8}$/.test(cleanedCEP);
}

const cep = context.local.data?.['form']?.['formData']?.['p_zip_code'];

return isCEPValido(cep);

