function formatRelativeNotificationTime(dateString, userTimeZone) {
  if (!dateString) {
    return ''; // Retorna string vazia se a data for nula ou indefinida
  }

  // Fuso horário padrão para o Brasil
  const defaultTimeZone = "America/Sao_Paulo";
  // Usa o fuso horário do usuário se fornecido e válido, senão usa o padrão
  const targetTimeZone = (userTimeZone && userTimeZone.trim() !== '') ? userTimeZone : defaultTimeZone;

  const then = new Date(dateString); // Data do evento
  const now = new Date();           // Data e hora atuais

  // Verifica se a data é válida
  if (isNaN(then.getTime())) {
    return "Data inválida";
  }

  // --- 1. Calcular diferenças de tempo (em milissegundos, minutos, horas) ---
  const diffMs = now.getTime() - then.getTime();
  const diffMins = Math.floor(diffMs / (1000 * 60));
  const diffHours = Math.floor(diffMs / (1000 * 60 * 60));

  // --- 2. Preparar para comparação de dias no fuso horário alvo ---
  // Formata as datas para YYYY-MM-DD no fuso horário alvo para uma comparação precisa de "dia"
  const dateOnlyFormatter = new Intl.DateTimeFormat('en-CA', { // 'en-CA' dá YYYY-MM-DD
    year: 'numeric', month: '2-digit', day: '2-digit', timeZone: targetTimeZone
  });

  const thenDateOnlyFormatted = dateOnlyFormatter.format(then);
  const nowDateOnlyFormatted = dateOnlyFormatter.format(now);

  // Cria objetos Date no UTC correspondente ao início do dia na TZ alvo para calcular diferença de dias exata
  const startOfThenDay = new Date(`${thenDateOnlyFormatted}T00:00:00.000Z`);
  const startOfNowDay = new Date(`${nowDateOnlyFormatted}T00:00:00.000Z`);

  // Calcula a diferença em dias inteiros
  const dayDifference = (startOfNowDay.getTime() - startOfThenDay.getTime()) / (1000 * 60 * 60 * 24);

  // --- 3. Formatadores para exibição de datas mais antigas ---
  const fullDateDisplayFormatter = new Intl.DateTimeFormat('pt-BR', {
    day: '2-digit', month: '2-digit', year: 'numeric', timeZone: targetTimeZone
  });
  const dayMonthDisplayFormatter = new Intl.DateTimeFormat('pt-BR', {
    day: '2-digit', month: '2-digit', timeZone: targetTimeZone
  });


  // --- 4. Lógica de Condicional para o Formato da Notificação ---
  if (dayDifference === 0) { // Se é o mesmo dia no fuso horário do usuário (Hoje)
    if (diffMins < 1) {
      return "Agora";
    } else if (diffMins < 60) {
      return `${diffMins} min atrás`;
    } else {
      return `${diffHours}H atrás`;
    }
  } else if (dayDifference === 1) { // Se foi ontem no fuso horário do usuário
    return "Ontem";
  } else if (dayDifference > 1 && dayDifference < 7) { // Entre 2 e 6 dias atrás
    return `${dayDifference} dias atrás`;
  } else { // Mais de 6 dias atrás
    if (then.getFullYear() === now.getFullYear()) {
      // Se for no mesmo ano, exibe DD/MM
      return dayMonthDisplayFormatter.format(then);
    } else {
      // Se for em ano diferente, exibe DD/MM/YYYY
      return fullDateDisplayFormatter.format(then);
    }
  }
}

