function generateBreadcrumbFromPath(pathString) {
  if (!pathString) {
    return []; // Retorna um array vazio se o caminho for nulo ou vazio
  }

  // --- MAPA DE EXCEÇÕES DE LABEL ---
  const labelExceptions = {
    usuario: "Usuário",
    usuarios: "Usuários", // Adicionando plural para maior robustez
    // Adicione outras exceções aqui se precisar
    cadastros: "Cadastros", // Exemplo de outra exceção se precisar
  };

  // Remove a barra inicial, se existir
  const cleanedPath = pathString.startsWith("/")
    ? pathString.substring(1)
    : pathString;

  // Divide o caminho em segmentos
  const segments = cleanedPath.split("/");

  const breadcrumbItems = [];
  let currentPath = "";

  // Itera sobre cada segmento para construir o array de breadcrumbs
  segments.forEach((segment) => {
    if (segment) {
      currentPath += "/" + segment;

      // --- LÓGICA DE AJUSTE DA LABEL ---
      const lowerCaseSegment = segment.toLowerCase();
      let label;

      // Verifica se o segmento está no nosso mapa de exceções
      if (labelExceptions[lowerCaseSegment]) {
        label = labelExceptions[lowerCaseSegment];
      } else {
        // Se não for uma exceção, apenas capitaliza a primeira letra
        label = segment.charAt(0).toUpperCase() + segment.slice(1);
      }

      // Adiciona o item ao array de breadcrumbs
      breadcrumbItems.push({
        label: label,
        path: currentPath,
      });
    }
  });

  return breadcrumbItems;
}

const path = variables["af2a8110-3dc4-4812-8d0b-207065c657fb"]?.["path"];

return generateBreadcrumbFromPath(path);

